import {useEffect, useState} from "react"
import Post from "./Post"

const PostsList = (user, title, text) => {
    const [data, setData] = useState([])

    useEffect(() => {
        fetch("/api/posts")
            .then(response => response.json())
            .then(json => setData(json.posts))
    }, [])

    console.log(data)
    return (
        <div>
            <p>{data[0]}</p>
            <p>{data[0].title}</p>
            <p>{data[0].text}</p>
            <ul>
                {data.map((post) => {
                    <Post user={post.userId} title={post.title} text={post.text}></Post>
                })}
            </ul>
        </div>
    )
}

export default PostsList;
