import {useEffect, useState} from "react"
import Post from "./Post"

const PostsList = (user, title, text) => {
    const [data, setData] = useState([])
    let key = 0;

    useEffect(() => {
        fetch("/api/posts")
            .then(response => response.json())
            .then(json => setData(json.posts))
    }, [])
    console.log(data);
    return (
        <div>
            <ul>
                {data.map((post) => (
                    <Post key="" user={post.userId} title={post.title} text={post.text}></Post>
                ))}
            </ul>
        </div>
    )
}

export default PostsList;
